import { useState,useReducer } from 'react';

const Example = () => {
  const [state,setState] = useState(0);
  const [rstate,dispatch] = useReducer(prev=>++prev, 0);

  const countUp = () => {
    setState(prev=>++prev);
  }
  return (
    <>
      <div>
        <h3>{state}</h3>
        <button onClick={countUp}>+</button>
      </div>
    </>
  );
};

export default Example;
